use strict; use warnings; use List::Util qw(shuffle); my %hash = (a => 1, b => 2, c => 3); #Preserve insertion order if using Perl 5.26+ my @keys = keys %hash; foreach my $key (@keys) { print "$key => $hash{$key}\n"; } #Sorting for predictable output foreach my $key (sort keys %hash) { print "$key => $hash{$key}\n"; } #Randomized order foreach my $key (shuffle keys %hash) { print "$key => $hash{$key}\n"; }